import React, { useEffect, useState } from 'react';

import axios from '../../service/axios';
import Select from '../../components/UI/Select';
import CorposCelestes from '../../components/CorposCelestes/CorposCelestes';

function Inicial(props) {
  const [options, setOptions] = useState([
    { value: 'planeta', displayValue: 'Planeta' },
    { value: 'estrela', displayValue: 'Estrela' },
    { value: 'constelação', displayValue: 'Constelação' },
    { value: 'galáxia', displayValue: 'Galáxia' },
    { value: 'buraco-negro', displayValue: 'Buraco Negro' },
    { value: 'perdido-no-tempo', displayValue: 'Perdido No Tempo' },
  ]);
  const [value, setValue] = useState('planeta');
  const [corposCelestes, setCorposCelestes] = useState([]);
  // const [corposCelestes, setCorposCelestes] = useState([
  //   {
  //     name: "Arcturus",
  //     fotoName: "arcturus",
  //     tipo: "estrela"
  //   },
  //   {
  //     name: "Marte",
  //     fotoName: "marte",
  //     tipo: "planeta",
  //   },
  //   {
  //     name: "Vênus",
  //     fotoName: "venus",
  //     tipo: "planeta"
  //   },
  //   {
  //     name: "Vega",
  //     fotoName: "vega",
  //     tipo: "planeta",
  //   },
  //   {
  //     name: "Spica",
  //     fotoName: "spica",
  //     tipo: "estrela",
  //   },
  //   {
  //     name: "Sagittarius A",
  //     fotoName: "sagittariusa",
  //     tipo: "buraco-negro",
  //   },
  //   {
  //     name: "Júpiter",
  //     fotoName: "jupiter",
  //     tipo: "planeta",
  //   },
  //   {
  //     name: "Andromeda",
  //     fotoName: "andromeda",
  //     tipo: "estrela",
  //   }
  // ]);
  const [filteredCorpos, setFilteredCorpos] = useState([]);

  useEffect(() => {
    axios.get('/destinos')
      .then(res => {
        const corpos = res.data.data
        setCorposCelestes(corpos);
        console.log(corposCelestes);
        setValue('planeta');
      })
      .catch(error => {
        console.log(error);
      })
  }, value);

  const selectChangedHandler = (event) => {
    event.preventDefault();

    let filtered = corposCelestes.filter(corpo => {
      return corpo.tipo === event.target.value;
    });
    console.log(filtered)

    setValue(event.target.value);
    setFilteredCorpos(filtered)
  }

  return (
    <div>
      {/* <Select
        options={options}
        value={value}
        change={selectChangedHandler}
      /> */}
      {/* <CorposCelestes
        corposCelestes={corposCelestes}
      /> */}
    </div>
  );
}

export default Inicial;
